
public class ComparisonOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 3;
		int y = 4;
		
		System.out.print((x == y) + " "); // false
		System.out.print((x != y) + " "); // true
		System.out.print((x > y) + " "); // false
		System.out.print((x < y) + " "); // true
		System.out.print((x <= y) + " "); // true
	}

}
